import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import api from '../services/api';

function CourseDetail() {
  const { courseId } = useParams();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchCourse() {
      try {
        const data = await api.getCourseById(courseId);
        setCourse(data);
      } catch (err) {
        setError('Failed to load course details');
        console.error(err);
      } finally {
        setLoading(false);
      }
    }

    fetchCourse();
  }, [courseId]);

  if (loading) {
    return (
      <div className="text-center py-10">
        <div className="spinner"></div>
        <p>Loading course details...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
        <p>{error}</p>
        <p className="mt-2">
          <Link to="/courses" className="text-red-700 font-bold">Return to courses</Link>
        </p>
      </div>
    );
  }

  if (!course) {
    return (
      <div className="text-center py-10">
        <p className="text-xl text-gray-700">Course not found</p>
        <p className="mt-2">
          <Link to="/courses" className="text-blue-500 hover:text-blue-700">Return to courses</Link>
        </p>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-6">
        <Link to="/courses" className="text-blue-500 hover:text-blue-700">
          &larr; Back to Courses
        </Link>
      </div>

      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <h1 className="text-3xl font-bold mb-2">{course.title}</h1>
        <p className="text-gray-600 mb-4">{course.description}</p>

        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <span className="text-gray-700 font-semibold">Course Code:</span>
            <span className="ml-2">{course.code}</span>
          </div>
          <div>
            <span className="text-gray-700 font-semibold">Status:</span>
            <span className={`ml-2 px-2 py-1 rounded text-xs font-semibold ${
              course.is_active ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
            }`}>
              {course.is_active ? 'Active' : 'Inactive'}
            </span>
          </div>
          {course.start_date && (
            <div>
              <span className="text-gray-700 font-semibold">Start Date:</span>
              <span className="ml-2">{new Date(course.start_date).toLocaleDateString()}</span>
            </div>
          )}
          {course.end_date && (
            <div>
              <span className="text-gray-700 font-semibold">End Date:</span>
              <span className="ml-2">{new Date(course.end_date).toLocaleDateString()}</span>
            </div>
          )}
        </div>
      </div>

      <h2 className="text-2xl font-bold mb-4">Modules</h2>

      {!course.modules || course.modules.length === 0 ? (
        <p className="text-gray-500">No modules available for this course.</p>
      ) : (
        <div className="space-y-4">
          {course.modules.map(module => (
            <div key={module.id} className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-xl font-semibold mb-2">{module.title}</h3>
              <p className="text-gray-600 mb-4">{module.description}</p>
              <Link
                to={`/modules/${module.id}`}
                className="bg-blue-500 hover:bg-blue-600 text-white py-2 px-4 rounded"
              >
                View Module
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default CourseDetail;
